const tweets = items.map(item => item.json);

const users = {};

for (const tweet of tweets) {
  const username = tweet.author?.screen_name || tweet.user?.screen_name || "unknown";
  const dateObj = new Date(tweet.created_at);
  const date = dateObj.toISOString().split("T")[0];
  const dayOfWeek = dateObj.getUTCDay(); // 0 = Sunday
  const hourOfDay = dateObj.getUTCHours(); // 0‚Äì23

  if (!users[username]) {
    users[username] = {
      tweetCount: 0,
      activeDays: new Set(),
      weekdayCount: Array(7).fill(0),
      hourCount: Array(24).fill(0),
      stats: {
        textOnly: 0,
        image: 0,
        video: 0,
        gif: 0,
        retweet: 0,
        quote: 0,
        threadStarter: 0
      },
      totalLikes: 0,
      totalRetweets: 0
    };
  }

  const user = users[username];
  user.tweetCount++;
  user.activeDays.add(date);
  user.weekdayCount[dayOfWeek]++;
  user.hourCount[hourOfDay]++;

  // Engagement
  user.totalLikes += tweet.favorite_count || 0;
  user.totalRetweets += tweet.retweet_count || 0;

  // Content type flags
  if (tweet.is_retweet) {
    user.stats.retweet++;
    return;
  }

  if (tweet.is_quote_status) {
    user.stats.quote++;
  }

  const media = tweet.media || {};
  const hasImage = !!media.photo;
  const hasVideo = !!media.video;
  const hasGif = !!media.animated_gif;

  if (hasImage) user.stats.image++;
  else if (hasVideo) user.stats.video++;
  else if (hasGif) user.stats.gif++;
  else user.stats.textOnly++;

  if (tweet.conversation_id === tweet.id) {
    user.stats.threadStarter++;
  }
}

// Helpers
function getMaxIndex(arr) {
  return arr.indexOf(Math.max(...arr));
}

const weekdayNames = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

const result = [];

for (const username in users) {
  const u = users[username];
  const tweetCount = u.tweetCount;
  const activeDays = u.activeDays.size;
  const tweetsPerDay = tweetCount / activeDays;
  const tweetsPerWeek = tweetsPerDay * 7;
  const engagementRate = (u.totalLikes + u.totalRetweets) / tweetCount;

  result.push({
    json: {
      username,
      tweetCount,
      avgTweetsPerDay: tweetsPerDay.toFixed(2),
      avgTweetsPerWeek: tweetsPerWeek.toFixed(1),
      activeDays,
      mostActiveWeekday: weekdayNames[getMaxIndex(u.weekdayCount)],
      mostActiveHourUTC: `${getMaxIndex(u.hourCount)}:00 - ${getMaxIndex(u.hourCount) + 1}:00 UTC`,
      engagementRate: engagementRate.toFixed(2),
      contentBreakdown: u.stats
    }
  });
}

return result;

